import React from "react";
import ProductItem from "./ProductItem";
import { getProducts } from "../repository";
import {
  FaCartPlus,
  FaUserPlus,
  FaCaretDown,
  FaBars,
  FaStoreAlt,
  FaSeedling,
  FaApple,
  FaCannabis,
  FaBeer,
  FaBreadSlice
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/cer.jpg";
import "./style.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    };
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });
  }

  render() {
    const { products } = this.state;
    return (
      <div className="container mt-5 ">
        <div class="row">
          <div className="col-lg-12">
            <Navbar
              collapseOnSelect
              className=" "
              expand="lg"
              style={nav}
              variant="dark"
            >
              <Navbar.Toggle
                aria-controls="responsive-navbar-nav"
                children={
                  <h1 style={ju}>
                    <FaBars />
                  </h1>
                }
                style={col}
              />
              <Navbar.Collapse id="responsive-navbar-nav">
                {/* <Nav className="mr-auto"></Nav> */}
                <Nav className="mr-5">
                  <Nav.Link
                    eventKey={2}
                    href=""
                    style={io}
                    className="text-dark mt-4"
                  >
                    <b className="lin">
                      <h6>
                        Fruits <FaApple />
                      </h6>
                      <hr />
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>
                        Vegetables <FaSeedling />
                      </h6>{" "}
                      <hr />
                      &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>
                        Cereals <FaStoreAlt />
                      </h6>
                      <hr />
                      &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>
                        Grains <FaCannabis />
                      </h6>
                      <hr />
                      &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>
                        Beverages <FaBreadSlice />
                      </h6>
                      <hr />
                      &nbsp;
                    </b>
                  </Nav.Link>
                  <Form.Group
                    as={Col}
                    md="4"
                    controlId="validationCustomUsername"
                  >
                    <Form.Label>Username</Form.Label>
                    <InputGroup>
                      <InputGroup.Prepend>
                        <InputGroup.Text id="inputGroupPrepend">
                          @
                        </InputGroup.Text>
                      </InputGroup.Prepend>
                      <Form.Control
                        type="text"
                        placeholder="Username"
                        aria-describedby="inputGroupPrepend"
                        required
                      />
                      <Form.Control.Feedback type="invalid">
                        Please choose a username.
                      </Form.Control.Feedback>
                    </InputGroup>
                  </Form.Group>
                </Nav>
              </Navbar.Collapse>
            </Navbar>
          </div>

          <div className="col-md-12 mt-4">
            {/* <h3 className="card-title">Available Products</h3> */}
            <hr />
            <div class="row">
              {products.map((product, index) => (
                <ProductItem product={product} key={index} />
              ))}
            </div>

            <hr />
          </div>
        </div>
      </div>
    );
  }
}
var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
};

var col = {
  backgroundColor: "#eef"
};

function CustomToggle({ children, eventKey }) {
  const decoratedOnClick = useAccordionToggle(eventKey, () =>
    console.log(eventKey)
  );

  return (
    <h4 class="text-center">
      <button
        type="submit"
        style={{
          backgroundColor: "transparent",
          textAlign: "center",
          border: "0px",
          outline: "none"
        }}
        onClick={decoratedOnClick}
      >
        {children}
      </button>
    </h4>
  );
}

var io = {
  paddingLeft: "15px"
};

var col = {
  color: "black",
  fontSize: "9px",
  padding: "10px"
};

var nav = {
  backgroundColor: "#eef"
};
var img = {
  width: "180px"
  // marginBottom: "10px"
};
var ui = {
  fontSize: "20px"
};
