import React from "react";
import { isAuthenticated, getCartProducts } from "../repository";
import { Card, Accordion, useAccordionToggle,Carousel } from "react-bootstrap/";
import { Redirect, Link } from "react-router-dom";
import swal from "sweetalert";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import {
  FaCartArrowDown,
  FaUserPlus,
  FaBars,
  FaCheckSquare,
  FaStore,
  FaSeedling,
  FaSearch,
  FaLock,
  FaArchive,
  Falorry
} from "react-icons/fa";
import "./style.css";
import Pagination from './pagination';

export default class Pay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      total: 0,
      pageOfItems: []
    };
    this.onChangePage = this.onChangePage.bind(this);
  }

  onChangePage(pageOfItems) {
         // update state with new page of items
         this.setState({ pageOfItems: pageOfItems });

     }

  componentWillMount() {
    let cart = localStorage.getItem("cart");
    if (!cart) return;
    getCartProducts(cart).then(products => {
      let total = 0;
      for (var i = 0; i < products.length; i++) {
          let rt = parseInt(products[i].qty.split("-")[0]);
        total += products[i].price * rt ;
      }
      this.setState({ products, total });
    });
  }

  render() {
      const {pageOfItems } = this.state;
    if (!isAuthenticated()) return <Redirect to="/login" />;
    const { products, total } = this.state;
    return (
      <div>

      <br />
      <br />
      <br />
      <div className="container  mb-5">
        <h5 style={mn} className="ml-4 rer">
          Delivery Details &nbsp; <FaLock />
        </h5>
        <hr />
        <div className="col-sm-8 col-sm-offset-2">
          <div className="panel panel-primary">
            <div className="panel-heading"></div>
            <div className="panel-body">
              <form onSubmit={this.submitSignup}>
                <div class="row">
                  <div class="col-md-6">
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Firstname:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control rer"
                        name="firstname"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>

                  <div class="col-md-6">
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Lastname:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control rer"
                        name="lastname"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>
                </div>

                <div class="row">
                  <div class="col-md-8">
                    {" "}
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Email:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control rer"
                        name="email"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>

                  <div class="col-md-4">
                    {" "}
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Phone:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control rer"
                        name="phone"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>
                </div>

                <div class="row">
                  <div class="col-md-4">
                    {" "}
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Phone 2:</h5>
                      </label>
                      <input
                        type="password"
                        className="form-control rer"
                        name="password"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>

                  <div class="col-md-8">
                    {" "}
                    <div className="form-group">
                      <label>
                        <h5 className="rer">Delivery Address:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control rer"
                        name="address"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  className=" rer btn btn-sm btn-outline-secondary"
                >
                Update
                </button>

              </form>
            </div>
          </div>
        </div>
      </div>

        </div>
    );
  }
}
var mn = {
  fontSize: "20px"
};

var uyh = {
  marginTop: "6px",
  width: "40px"
};
var uyw = {
