import React from "react";
import swal from "sweetalert";
import { FaCartPlus, FaUserPlus, FaCaretDown } from "react-icons/fa";
import { Link } from "react-router-dom";
import { Card, Accordion, useAccordionToggle,Carousel } from "react-bootstrap/";
import cer from "./image/pro2.jpg";
import "./style.css";

export default class ProductItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1
    };
  }

  handleInputChange = event =>
    this.setState({ [event.target.name]: event.target.value });

  addToCart = () => {


    swal("Select a Size", {
      buttons: {
        M: {
          text: "M",
          value: "M"
        },
        X: {
          text: "X",
          value: "X"
        },
        XL: {
          text: "XL",
          value: "XL"
        },
        cancel: true
      }
    }).then(value => {
      switch (value) {
        case "M":
            let cart = localStorage.getItem("cart")
              ? JSON.parse(localStorage.getItem("cart"))
              : {};
        console.log(cart);
            let id = this.props.product.id.toString();
            cart[id] = cart[id] ? cart[id] : 0;
            console.log(cart[id]);
            let qty = cart[id] + parseInt(this.state.quantity);
            console.log(qty);
            // alert(qty);
            // alert(cart[id]);
            if (this.props.product.available_quantity < qty) {
              swal(
                "Not Enough Item",
                "We Don't Have Enough Of this Product Available!!",
                "error"
              );
            } else {
              cart[id] = qty +"m";
              swal("Added To Cart!", "Product Added To Cart!", "success");
            }

            localStorage.setItem("cart", JSON.stringify(cart));
          break;

        default:

          break;


}


});












  };



  render() {
    const { product } = this.props;

    return (
      <div className="col-md-2 mb-5 card-item  ttui">
        <Card class="card" >

        <Carousel controls={true} indicators={false} interval={false} pause={true} slide={true} fade={false} >
          <Carousel.Item>
            <div className="container">
            <h4 class="text-center">
              {" "}
              <img
                className="image-responsive hov2 hov"
                src={require(`${product.image_path}`)}
                alt="img"
              />
            </h4>
            </div>
          </Carousel.Item>

          <Carousel.Item>
            <div className="container">
            <h4 class="text-center">
              {" "}
              <img
                className="image-responsive hov2 hov"
                src={require(`${product.image_path_2}`)}
                alt="img"
              />
            </h4>
            </div>
          </Carousel.Item>
        </Carousel>

          <Card.Body>
            <h4
              class="text-dark text-center rtt"
              style={{
                fontSize: "12px",
                fontFamily: "noir",

              }}
            >
              <b class="rtt"> {product.name}</b>
            </h4>

            <hr />

            <h5
              class="text-dark text-center "
              style={{ fontSize: "12px", fontFamily: "Work sans,San serif" }}
            >
              {product.available_quantity > 0 ? (
                <div>
                  {/* <input
                    type="number"
                    value={this.state.quantity}
                    name="quantity"
                    onChange={this.handleInputChange}
                    className="float-right form-control"
                    style={{
                      fontWeight: "bold",
                      width: "60px",
                      height: "30px",
                      marginRight: "0px",
                      borderRadius: "3px",
                      marginTop: "7px"
                    }}
                  /> */}
                  {/* <h5
                    class="text-dark text-center"
                    style={{ fontSize: "14px" }}
                  >
                    {" "}
                    <span class="badge badge-secondary">
                      {" "}
                      {product.available_quantity}
                    </span>{" "}
                    Item Available
                  </h5> */}
                </div>
              ) : (
                <p
                  className="text-danger"
                  style={{
                    fontSize: "18px",
                    fontFamily: "noir"
                  }}
                ></p>
              )}
            </h5>
            {/* <hr /> */}

            <h5
              class="text-dark text-center gty"
              style={{ fontSize: "13px", fontFamily: "noir" }}
            >
              ₦{product.price}
            </h5>

            {product.available_quantity > 0 ? (
              <h4 class="text-dark text-center ">
                <button
                  style={{ fontSize: "11px", fontFamily: "noir" }}
                  className="btn btn-outline-secondary btn-sm"
                  onClick={this.addToCart}
                >
                  Add To Cart
                </button>
                <br/>



              </h4>
            ) : (
              <h4 class="text-dark text-center">
                <button
                  style={{ fontSize: "11px", fontFamily: "noir" }}
                 className="btn btn-outline-secondary btn-sm disabled">
                  Out of Stock
                </button>
              </h4>
            )}
          </Card.Body>
        </Card>
      </div>
    );
  }
}



var col = {
  backgroundColor: "#eef"
};
