import React from "react";
import ProductItem from "./ProductItem";
import { getProducts } from "../repository";
import { FaCartPlus, FaUserPlus, FaCaretDown } from "react-icons/fa";
import { Link } from "react-router-dom";
import { Card, Accordion, useAccordionToggle } from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/cer.jpg";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    };
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });
  }

  render() {
    const { products } = this.state;
    return (
      <div className="container mt-5 ">
        <div class="row">
          <div className="col-md-3 mt-5">
            <Accordion defaultActiveKey="1">
              <div class="col-lg-12 col-md-2 col-sm-2 mb-3">
                <Card>
                  <Card.Header style={col}>
                    <CustomToggle eventKey="0">
                      <h4 class="text-dark text-center">
                        Category&nbsp;&nbsp; <FaCaretDown />
                      </h4>

                      {/* <h1 style={ui}>
                        <FaCaretDown />
                      </h1> */}
                    </CustomToggle>
                  </Card.Header>
                </Card>
              </div>
              <Accordion.Collapse eventKey="0">
                <Card>
                  <Card.Body>
                    <h4 class="text-dark text-center">Fruits</h4>
                    <hr />
                    <h4 class="text-dark text-center">Vegetables</h4>
                    <hr />
                    <h4 class="text-dark text-center">Cereals</h4>
                    <hr />
                    <h4 class="text-dark text-center">Grains</h4>
                    <hr />
                    <h4 class="text-dark text-center">Beverages</h4>
                    <hr />
                  </Card.Body>
                </Card>
              </Accordion.Collapse>
            </Accordion>

            <br />
            <br />
          </div>
          <div className="col-md-9">
            <h3 className="card-title">Available Products</h3>
            <hr />
            {/* {products.map((product, index) => (
              <ProductItem product={product} key={index} />
            ))} */}

            <div className="row">
              <div className="col-md-4">
                <Card>
                  <Card.Header style={col}>
                    <h4 class="text-center">
                      {" "}
                      <img
                        className="image-responsive "
                        style={img}
                        src={cer}
                        alt="Third slide"
                      />
                    </h4>
                  </Card.Header>

                  <Card.Body>
                    <h4 class="text-dark text-center">Name</h4>
                    <hr />
                    <h4 class="text-dark text-center">Category</h4>
                    <hr />
                    <h4 class="text-dark text-center">Price(per)</h4>
                    <hr />
                    <h4 class="text-dark text-center">Indulge</h4>
                    <hr />
                    <h4 class="text-dark text-center">Views|More|Pay</h4>
                    <hr />
                  </Card.Body>
                </Card>
              </div>

              <div className="col-md-4">
                <Card>
                  <Card.Header style={col}>
                    <h4 class="text-center">Image</h4>
                  </Card.Header>
                  <Card.Body>
                    <h4 class="text-dark text-center">Name</h4>
                    <hr />
                    <h4 class="text-dark text-center">Category</h4>
                    <hr />
                    <h4 class="text-dark text-center">Price(per)</h4>
                    <hr />
                    <h4 class="text-dark text-center">Indulge</h4>
                    <hr />
                    <h4 class="text-dark text-center">Views|More|Pay</h4>
                    <hr />
                  </Card.Body>
                </Card>
              </div>

              <div className="col-md-4">
                <Card>
                  <Card.Header style={col}>
                    <h4 class="text-center">Image</h4>
                  </Card.Header>

                  <Card.Body>
                    <h4 class="text-dark text-center">Name</h4>
                    <hr />
                    <h4 class="text-dark text-center">Category</h4>
                    <hr />
                    <h4 class="text-dark text-center">Price(per)</h4>
                    <hr />
                    <h4 class="text-dark text-center">Indulge</h4>
                    <hr />
                    <h4 class="text-dark text-center">Views|More|Pay</h4>
                    <hr />
                  </Card.Body>
                </Card>
              </div>
            </div>

            <hr />
          </div>
        </div>

        <br />
        <br />
        <br />
      </div>
    );
  }
}

var col = {
  backgroundColor: "#eef"
};

function CustomToggle({ children, eventKey }) {
  const decoratedOnClick = useAccordionToggle(eventKey, () =>
    console.log(eventKey)
  );

  return (
    <h4 class="text-center">
      <button
        type="submit"
        style={{
          backgroundColor: "transparent",
          textAlign: "center",
          border: "0px",
          outline: "none"
        }}
        onClick={decoratedOnClick}
      >
        {children}
      </button>
    </h4>
  );
}

var ui = {
  fontSize: "20px"
};
