import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";
import { Link } from "react-router-dom";
import {
  FaCart,
  FaUserPlus,
  FaBars,
  FaCheckSquare,
  FaStore
} from "react-icons/fa";
import logo from "./image/logo.png";
import { isAuthenticated } from "../repository";
import "./style.css";
class Navigator extends Component {
  logOut() {
    localStorage.removeItem("x-access-token");
    window.location = "/";
  }

  render() {
    const auth = isAuthenticated();
    return (
      <div class="">
        <Navbar
          collapseOnSelect
          className="  "
          expand="lg"
          style={nav}
          variant="dark"
        >
          <Link to="/">
            {/* <Navbar.Brand href="#home" style={all} className="text-dark "> */}
            <Nav className="ml-5">
              <h5>#</h5>
            </Nav>
            {/* </Navbar.Brand> */}
          </Link>
          <Navbar.Toggle
            aria-controls="responsive-navbar-nav"
            children={
              <h1>
                <FaBars />
              </h1>
            }
            style={col}
          />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto"></Nav>
            <Nav className="mr-5">
              <Nav.Link
                eventKey={2}
                href="/market"
                style={io}
                className="text-dark mt-4"
              >
                <b className="lin">
                  <h6>
                    Market <FaStore />
                  </h6>
                </b>
              </Nav.Link>
              <Nav.Link href="/cart" style={io} className="text-dark mt-4">
                <b className="lin">
                  <h6>
                    Cart <FaCart />
                  </h6>{" "}
                  &nbsp;
                </b>
              </Nav.Link>
              <Nav.Link href="/checkout" style={io} className="text-dark mt-4">
                <b className="lin">
                  <h6>Checkout</h6> &nbsp;
                </b>
              </Nav.Link>

              {!auth ? (
                <Nav.Link
                  eventKey={2}
                  href="/login"
                  style={io}
                  className="text-dark mt-4"
                >
                  <b className="lin">
                    <h6>Login</h6> &nbsp;
                  </b>
                </Nav.Link>
              ) : (
                <Nav.Link
                  eventKey={2}
                  onClick={this.logOut}
                  style={io}
                  className="text-dark mt-4"
                >
                  <b className="lin">
                    <h6>Logout</h6>
                  </b>
                </Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

var io = {
  paddingLeft: "15px"
};

var col = {
  color: "black",
  fontSize: "9px",
  padding: "10px"
};

var nav = {
  backgroundColor: "#eef"
};
var img = {
  width: "180px"
  // marginBottom: "10px"
};
export default Navigator;
