import React from "react";
import "./style.css";
import swal from "sweetalert";
import { Card, Accordion, useAccordionToggle } from "react-bootstrap/";
export default class CartItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1
    };
  }

  addToCart = () => {
    let cart = localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : {};

    let id = this.props.product.id.toString();
    cart[id] = cart[id] ? cart[id] : 0;
    let qty = cart[id] + parseInt(this.state.quantity);
    // alert(qty);
    // alert(cart[id]);
    if (this.props.product.available_quantity < qty) {
      swal(
        "Not Enough Item",
        "We Don't Have Enough Of this Product Available!!",
        "error"
      );
    } else {
      cart[id] = qty;
      swal("+1 to Cart!", "Prooduct Added", "success");
    }

    localStorage.setItem("cart", JSON.stringify(cart));
  };

  removefromCart = (e) => {
    e.preventDefault();
    let cart = localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : {};

    let id = this.props.product.id.toString();
    cart[id] = cart[id] ? cart[id] : 0;
    let qty = cart[id] - parseInt(this.state.quantity);
    // alert(qty);
    // alert(cart[id]);

    if (this.props.product.available_quantity < qty) {
      swal(
        "Not Enough Item",
        "We Don't Have Enough Of this Product Available!!",
        "error"
      );
    } else {
      cart[id] = qty;
      swal("-1 from Cart!", "Prooduct Removed", "success");
    }

    localStorage.setItem("cart", JSON.stringify(cart));
  };


  render() {
    const { product } = this.props;
    return (
      <div className="col-md-2 mb-5 card-item rft">
        <Card class="card mb-5">
          {/* <Card.Header style={{ backgroundColor: "#fdfdf9" }}> */}

          {/* </Card.Header> */}

          <Card.Body >
            <h4
              class="text-dark text-center rtt"
              style={{
                fontSize: "20px",
                fontFamily: "noir",
                textTransform: "capitalize"
              }}
            >
              <b> {product.name}</b>
            </h4>

            <hr />

            {product.available_quantity > 0 ? (
              <h4 class="text-dark text-center">
                <button
                  className="btn btn-outline-secondary btn-sm"
                  onClick={this.addToCart}
                >
                  +
                </button>&nbsp;

                <button
                  className="btn btn-outline-secondary btn-sm"
                  onClick={this.removefromCart}
                >
                  -
                </button>
                <br/>
                <br/>
              </h4>

            ) : (
              <h4 class="text-dark text-center">
                <button className="btn btn-outline-secondary btn-sm disabled">
                  Out of Stock
                </button>
              </h4>
            )}
            <h5
              class="text-dark text-center"
              style={{ fontSize: "13px", fontFamily: "noir" }}
            >
              â‚¦{product.price}
            </h5>

            <h5 className="card-text text-center" style={fv}>
              <span className="card-text text-secondary fv">
                Quantity:
                &nbsp;<span class="badge badge-secondary">{product.qty}</span>
              </span>
            </h5>

            <h4 class="text-center">
              {" "}
              <button
                className="btn btn-sm  btn-outline-secondary"
                onClick={() => this.props.remove(product)}
              >
                Remove
              </button>
            </h4>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
var img = {
  width: "170px",
  height: "170px",
  marginTop: "10px"
};
var fv = {
  fontSize: "11px"
};
