import React from "react";
import ProductItem from "./ProductItem";
import swal from "sweetalert";
import {
  getProducts,
  product,
  category,
  getCategoryProduct,
  Type,
  getSubCat
} from "../repository";
import Spinner from "react-bootstrap/Spinner";
import {
  FaArrowAltCircleDown,
  FaSearch,
  FaServicestack,
  FaBars,
  FaStore,
  FaCaretDown,
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col,
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
import "./footer.css";
import Pagination from './pagination';

export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      loader: false,
      category: null,
      search: false,
      searchtext: "",
pageOfItems: [],
currentPage: 1,
      productsPerPage: 8,
      type: [],
      subcart:[],
      dr: "none",
      cat_color: "black"

    };
    this.onchange = this.onchange.bind(this);
      this.onChangePage = this.onChangePage.bind(this);
          this.handleClick2 = this.handleClick2.bind(this);
                    this.getSubCart= this.getSubCart.bind(this);
                      this.turnoff= this.turnoff.bind(this)
  }

  getSubCart(id) {


        getSubCat(id).then(subcart => {
          this.setState({ subcart });

          this.setState({ dr: "" });

          console.log(subcart);
        }).catch(err =>{
          console.log(err);
        });




   }

  handleClick2(event) {
     this.setState({
       currentPage: Number(event.target.id)
     });
   }

   turnoff(event) {
      this.setState({
        dr: "none"
      });
    }



  onChangePage(pageOfItems) {
         // update state with new page of items
         this.setState({ pageOfItems: pageOfItems });

     }




  componentWillMount() {


    getProducts().then(products => {
      this.setState({ products });
      this.setState({ dr: "none" });
    });

    Type().then(type=> {
      this.setState({ type });
    });

    category().then(category => {
      this.setState({ category });
      // console.log(this.state.category);
    });
  }

  getall = () => {
    getProducts().then(products => {
        this.setState({ search: false });
          this.setState({ searchtext: "" });
          this.setState({ dr: "none" });
      this.setState({ products });
    });
  };


  handleClick = ev => {
    ev.preventDefault();
    getCategoryProduct(ev.currentTarget.value).then(products => {
      if (products) {
        this.setState({ search: false });
        this.setState({ products });
        this.setState({ currentPage:1 });
        this.setState({ dr: "none" });
        // console.log(this.state.products);
      }

    });
  };

  onchange(event) {
    event.preventDefault();
    if (event.target.value) {
      let product_name = event.target.value;
      this.setState({ searchtext: event.target.value });
      product(product_name).then(products => {
        this.setState({ pageOfItems: [] });
        this.setState({ products });
        this.setState({ search: true });
      });
    }else{
  this.setState({ search: false });
    this.setState({ searchtext: "" })
    // window.location ="/";
    getProducts().then(products => {
      this.setState({ products });
      this.setState({ pageOfItems: [] });
    });
    }
  }

  render() {

    const { category } = this.state;
    const { search } = this.state;
    const { searchtext } = this.state;
    const {pageOfItems, type,subcart, dr } = this.state;


    const { products, currentPage, productsPerPage } = this.state;


    const rendersubcart = <div id="edc"><Card class="card3" style={{display:dr}}>

    <Card.Body >
    <p>All</p>
    <p class="xer"><button class="btn btn-secondary btn-sm"    onClick={this.turnoff} >x</button></p>

       {subcart.map((sub,index) => {
return <div>
<p style={{fontSize:"13px"}}>{sub.sub_cat_name}</p>

</div>;
  })}
  </Card.Body>
       </Card></div>;



      const indexOfLastproducts = currentPage * productsPerPage;
   const indexOfFirstproducts = indexOfLastproducts - productsPerPage;
   const currentproducts = products.slice(indexOfFirstproducts, indexOfLastproducts);

   const renderproducts = currentproducts.map((products) => {

      return <ProductItem product={products} type={type} key={products.id} />;

   });



   // Logic for displaying page numbers
   const pageNumbers = [];
   for (let i = 1; i <= Math.ceil(products.length / productsPerPage); i++) {
     pageNumbers.push(i);
   }

   const renderPageNumbers = pageNumbers.map(number => {
     return (
       <li
       className="pager"
         key={number}
         id={number}
         onClick={this.handleClick2}
       >
         {number}
       </li>
     );
   });
    return (
      <div className="">
        <br />
        <div class="gyu">

          <div class="uio nav">
            <div className="container ">
              <div className="row">






                <div className="col-lg-6">
                  <Navbar
                    collapseOnSelect
                    className="navbar-expand mlp"
                    expand="lg"
                    variant="dark"
                  >
                <Navbar.Toggle
                  aria-controls="responsive-navbar-nav"
                  children={
                    <p className="eri"
                      style={{
