import React from "react";
import ProductItem from "./ProductItem";
import {
  getProducts,
  product,
  category,
  getCategoryProduct
} from "../repository";
import Spinner from "react-bootstrap/Spinner";
import {
  FaArrowAltCircleDown,
  FaSearch,
  FaServicestack,
  FaBars,
  FaStore,
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/pro1.jpg";
import "./style.css";
import "./footer.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: "",
      loader: false,
      category: null,
      search: false,
      searchtext: "",
      currentPage: 1,
     productsPerPage: 12
    };
    this.onchange = this.onchange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }
  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });

    category().then(category => {
      this.setState({ category });
      // console.log(this.state.category);
    });
  }

  getall = ev => {
    ev.preventDefault();
    getProducts().then(products => {
      this.setState({ products });
    });
  };


  handleClick = ev => {
    ev.preventDefault();
    getCategoryProduct(ev.currentTarget.value).then(products => {
      if (products) {
        this.setState({ search: false });
        this.setState({ products });
        // console.log(this.state.products);
      }


    });
  };
  onchange(event) {
    event.preventDefault();
    if (event.target.value) {
      let product_name = event.target.value;
      this.setState({ searchtext: event.target.value });
      product(product_name).then(products => {
        this.setState({ products });
        this.setState({ search: true });
      });
    } else {
      getProducts().then(products => {
        this.setState({ products });
        this.setState({ search: false });
      });
    }
  }

  render() {
    const { products } = this.state;
    const { category } = this.state;
    const { search } = this.state;
    const { searchtext } = this.state;
      const { currentPage } = this.state;
        const { productsPerPage } = this.state;
        const indexOfLastproduct = currentPage * productsPerPage;
            const indexOfFirstproduct = indexOfLastproduct - productsPerPage;
            const currentproducts = products.slice(indexOfFirstproduct, indexOfLastproduct);
            const pageNumbers = [];
   for (let i = 1; i <= Math.ceil(products.length / productsPerPage); i++) {
     pageNumbers.push(i);
   }
