   });
   const renderPageNumbers = pageNumbers.map(number => {
        return (
          <li
            key={number}
            id={number}
            onClick={this.handleClick}
          >
            {number}
          </li>
        );
      });
    return (
      <div className="">
        <br />
        <div class="gyu">
          <div class="uio nav">
            <div className="container ">
              <div className="row">
                <div className="col-lg-6">
                  <Navbar
                    collapseOnSelect
                    className="navbar-expand mlp"
                    expand="lg"
                    variant="dark"
                  >
                <Navbar.Toggle
                  aria-controls="responsive-navbar-nav"
                  children={
                    <p className="eri"
                      style={{
                        fontFamily: "noir",
                        fontSize: "29px"
                      }}
                    >
                      <FaStore />
                    </p>
                  }
                  style={col}
                />
                    <Navbar.Collapse id="responsive-navbar-nav" >
                    <Nav className="mr-auto"></Nav>

                      <Nav className=" mt-0">

                      <button
                        onClick={this.getall}

                        style={btu}
                      >
                        <h2
                          class="rtt  "
                          style={{
                            fontSize: "20px",
                            fontFamily: "noir"
                          }}
                        >
                        <b class="ico"><FaStore/> </b>&nbsp; &nbsp;
                        &nbsp;
                        </h2>
                      </button>

                        {category == null ? (
                          <h2
                            class="rtt mt-3 "
                            style={{
                              fontSize: "16px",
                              fontFamily: "noir"
                            }}
                          >
                            <b >Loading Available Category!!! </b>&nbsp; &nbsp;
                            &nbsp;
                          </h2>
                        ) : (



                          category.map((category, index) => (
                            <button
                              onClick={this.handleClick}
                              value={category.id}
                              style={btu}
                            >
                              <h2
                                class="rtt  "
                                style={{
                                  fontSize: "16px",
                                  fontFamily: "noir"
                                }}
                              >
                                <b class="rer">{category.category_name} </b>&nbsp; &nbsp;
                                &nbsp;
                              </h2>
                            </button>
                          ))
                        )}
                      </Nav>
                    </Navbar.Collapse>
                  </Navbar>
                </div>

                <div className="col-lg-6 float-left mt-1">
                  <Nav.Link href="" className="text-dark  ">
                    <Form.Group as={Col} md="12" className="sio">
                      <InputGroup>
                        <InputGroup.Prepend>
                          <InputGroup.Text style={uyh} id="inputGroupPrepend">
                            <FaSearch style={{ color: "#fdfdfe" }} />
                          </InputGroup.Text>
                        </InputGroup.Prepend>
                        <Form.Control
                          type="text"
                          placeholder="Enter a Product Name Here!"
                          aria-describedby="inputGroupPrepend"
                          onChange={this.onchange}
                          style={uyw}
                          class="sio ml-5 float-left"
                          required
                        />
                      </InputGroup>
                    </Form.Group>
                  </Nav.Link>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container mt-5 ">
          <div class="row">
            <div className="col-lg-12"></div>

            <div className="col-md-12 mt-4">
              {search === true ? (
                <h3
                  style={{ fontSize: "15px" }}
                  className="card-title mb-5 ml-5"
                >
                  {products.length} Search Results for {searchtext} :
                </h3>
              ) : (
                <h2></h2>
              )}
              {/* <hr /> */}

              <div class="row " id="products">
                {!products.length  ? (
                  <h1 className="text-center ml-5 mb-4" style={{ fontSize: "17px" }}>
                  No Products Found!!
                  </h1>
                ) : (
                  currentproducts.map((product, index) => (
                    <ProductItem product={product} key={index} />
                  ))
                )}
              </div>

              <div>
                     <ul>
                       {renderTodos}
                     </ul>
                     <ul id="page-numbers">
                       {renderPageNumbers}
                     </ul>
                   </div>


            </div>
          </div>
        </div>
      </div>
    );
  }
}
