import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Navbar,
  Container,
  Nav,
  NavDropdown,
  Form,
  InputGroup,
  Col
} from "react-bootstrap";
import { Link } from "react-router-dom";
import "./style.css";
class Footer extends Component {
  render() {
    return (
      <div className="App">
        <Navbar collapseOnSelect expand="w-100" style={nav} variant="dark">
          <div className="container">
            <div className="row mt-4 mb-5">
              <div className="col-md-6 mt-4">
                <b>Quick Links</b>
                <br />
                Cart
                <br />
                Market
              </div>
              <div className="col-md-4 mt-4">
                <b>Talk About Your Bussiness</b>
                <br />
                <br />
                Share store details, promotions, or brand content with your
                customers.
              </div>
              <div className="col-md-4">
                <Form.Group as={Col} md="12" className="ml-5">
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text id="inputGroupPrepend">
                        <FaSearch />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <Form.Control
                      type="text"
                      placeholder="Product Name"
                      aria-describedby="inputGroupPrepend"
                      onChange={this.onchange}
                      class="sio"
                      required
                    />
                  </InputGroup>
                </Form.Group>
              </div>
            </div>
          </div>
          {/* <h6 className="p-2 mt-2">AgroBased Nigeria Â© 2020</h6> */}
        </Navbar>
      </div>
    );
  }
}

var nav = {
  backgroundColor: "#eef9",
  color: "black"
};

export default Footer;
