import React from "react";
import { Link } from "react-router-dom";
import { getCartProducts } from "../repository";
import CartItem from "./CartItem";
import "bootstrap/dist/css/bootstrap.min.css";
import swal from "sweetalert";
import Pagination from './pagination';
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import {
  FaCartArrowDown,
  FaUserPlus,
  FaBars,
  FaCheckSquare,
  FaStore,
  FaSeedling,
  FaSearch,
  FaLock,

} from "react-icons/fa";
export default class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      total: 0,
      pageOfItems: [],
      qty:""
    };
        this.onChangePage = this.onChangePage.bind(this);
  }


  onChangePage(pageOfItems) {
         // update state with new page of items
         this.setState({ pageOfItems: pageOfItems });

     }


  componentWillMount() {

    let cart = localStorage.getItem("cart");

    if (!cart) return;
    getCartProducts(cart).then(products => {
      let total = 0;
      for (var i = 0; i < products.length; i++) {
        total += products[i].price * products[i].qty;
      }
      this.setState({ products, total });
    });
  }

  removeFromCart = product => {
    swal("Are You sure you want to remove product?", {
      buttons: {
        cancel: "default",
        ok: {
          text: "Remove",
          value: "ok"
        },
        cancel: true
      }
    }).then(value => {
      switch (value) {
        case "ok":
          let products = this.state.products.filter(
            item => item.id !== product.id
          );
          let cart = JSON.parse(localStorage.getItem("cart"));
          delete cart[product.id.toString()];
          localStorage.setItem("cart", JSON.stringify(cart));
          let total = this.state.total - product.qty * product.price;
          this.setState({ products, total });

          swal("Product has been removed!");
          break;

        default:

          break;
      }
    });
  };


    addToCart =  product => {
    let products = this.state.products.filter(
      item => item.id !== product.id
    );
    let cart = localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : {};

    let id = product.id.toString();
    cart[id] = cart[id] ? cart[id] : 0;
    let qty = cart[id] + 1;
    alert(qty);
    this.setState({qty:qty});
    alert(cart[id]);
    if (product.available_quantity < qty) {
      swal(
        "Not Enough Item",
        "We Don't Have Enough Of this Product Available!!",
        "error"
      );
    } else {
      cart[id] = qty;
      let total = this.state.total + 1 * product.price;
      this.setState({...products, total });
        localStorage.setItem("cart", JSON.stringify(cart));
      swal("Added To Cart!", "Product Added To Cart!", "success");
    }


    };



    removefromCart = product => {
      let products = this.state.products.filter(
        item => item.id !== product.id
      );
      let cart = localStorage.getItem("cart")
        ? JSON.parse(localStorage.getItem("cart"))
        : {};

      let id = product.id.toString();
      cart[id] = cart[id] ? cart[id] : 0;
      let qty = cart[id] - 1;
      // alert(qty);

      // alert(cart[id]);
      if (product.available_quantity < qty) {
        swal(
          "Not Enough Item",
          "We Don't Have Enough Of this Product Available!!",
          "error"
        );
      } else {
        cart[id] = qty;
        let total = this.state.total - 1 * product.price;
        // let product_qty = product.available_quantity - 1;
        this.setState({...products, total });
        swal("Added To Cart!", "Product Added To Cart!", "success");
      }

      localStorage.setItem("cart", JSON.stringify(cart));
    };


  clearCart = () => {
    localStorage.removeItem("cart");
    this.setState({ products: [] });
    swal("Your Cart Has Been Cleared!!!", "", "success");
  };

  render() {
    const { products, total } = this.state;
      const {pageOfItems,qty } = this.state;

    return (
      <div>


        <br />
        <br />
        <br />

        <div class="container ">
          <div class="row" id="products">


           {pageOfItems.map(item =>
             <CartItem
               product={item}
               qty={this.state.qty}
               remove={this.removeFromCart}
               remove2={this.removefromCart}
               add={this.addToCart}
               key={item.id}
             />
           )}





          </div>
          <Pagination items={this.state.products} onChangePage={this.onChangePage} />


          <div class="container">
            {products.length ? (
              <div>
                <h5 class="text-dark" style={{ fontSize: "20px" }}>
                  <b>Total Amount:</b>
                  <span
                    className="float-right text-dark"
                    style={{ fontSize: "21px" }}
                  >
                    <b> â‚¦{total}</b>
                  </span>
                </h5>
                <hr />
              </div>
            ) : (
              ""
            )}

            {!products.length ? (
              <h3 className="text-dark">
                <b>No item on the cart!!!</b>
                <hr/>
              </h3>
            ) : (
              ""
            )}


            <Link to="/checkout">
              <button className="btn btn-sm btn-outline-secondary float-right">
                Checkout
              </button>
            </Link>
            <button
              className="btn btn-outline-secondary btn-sm float-left"
              onClick={this.clearCart}
              style={{ marginRight: "10px" }}
            >
              Clear Cart
            </button>
            <br />

            <br />
            <br />
          </div>
        </div>
      </div>
    );
  }
}

