  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col,
  Carousel
} from "react-bootstrap/";
import image from "./image/watch41.png";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      quantity: 1,
      dr4:"none",
      drt:"",
      product:[]
    };
  }


componentDidMount(){
    let id = localStorage.getItem("pro_id");
