import React from "react";
import swal from "sweetalert";
import { FaCartPlus, FaUserPlus, FaCaretDown } from "react-icons/fa";
import { Link } from "react-router-dom";
import { Card, Accordion, useAccordionToggle } from "react-bootstrap/";
import cer from "./image/cer.jpg";
export default class ProductItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1
    };
  }

  handleInputChange = event =>
    this.setState({ [event.target.name]: event.target.value });

  addToCart = () => {
    let cart = localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : {};

    let id = this.props.product.id.toString();
    cart[id] = cart[id] ? cart[id] : 0;
    let qty = cart[id] + parseInt(this.state.quantity);
    if (this.props.product.available_quantity < qty) {
      swal(
        "Not Enough Item",
        "We Don't Have Enough Of this Product Available!!",
        "error"
      );
    } else {
      cart[id] = qty;
      swal("Added To Cart!", "Prooduct Added To Cart!", "success");
    }

    localStorage.setItem("cart", JSON.stringify(cart));
  };

  render() {
    const { product } = this.props;
    return (
      <div className="col-md-3 mb-5">
        <Card>
          <Card.Header style={col}>
            <h4 class="text-center">
              {" "}
              <img
                className="image-responsive hov "
                style={img}
                src={cer}
                alt="Third slide"
              />
            </h4>
          </Card.Header>

          <Card.Body>
            <h4 class="text-dark text-center">{product.name}</h4>
            <hr />
            <h5 class="text-dark text-center">AgricPort Limited</h5>
            <hr />

            <h5 class="text-dark text-center ">
              {product.available_quantity > 0 ? (
                <div>
                  {/* <input
                    type="number"
                    value={this.state.quantity}
                    name="quantity"
                    onChange={this.handleInputChange}
                    className="float-right form-control"
                    style={{
                      fontWeight: "bold",
                      width: "60px",
                      height: "30px",
                      marginRight: "0px",
                      borderRadius: "3px",
                      marginTop: "7px"
                    }}
                  /> */}
                </div>
              ) : (
                <p className="text-danger"> Product is out of stock </p>
              )}
            </h5>
            {/* <hr /> */}

            <hr />
            <h5 class="text-dark text-center">₦{product.price}</h5>
            <hr />
            <h4 class="text-dark text-center">
              <button
                className="btn btn-outline-info btn-sm"
                onClick={this.addToCart}
              >
                <b class="text-center ">Add To Cart</b>
              </button>
            </h4>
          </Card.Body>
        </Card>
      </div>
    );
  }
}

var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
};

var col = {
  backgroundColor: "#eef"
};
