import React from "react";
import ProductItem from "./ProductItem";
import {
  getProducts,
  product,
  category,
  getCategoryProduct
} from "../repository";
import Spinner from "react-bootstrap/Spinner";
import {
  FaArrowAltCircleDown,
  FaSearch,
  FaServicestack,
  FaBars,
  FaStore,
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/pro1.jpg";
import "./style.css";
import "./footer.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: "",
      loader: false,
      category: null,
      search: false,
      searchtext: ""
    };
    this.onchange = this.onchange.bind(this);
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });

    category().then(category => {
      this.setState({ category });
      // console.log(this.state.category);
    });
  }

  getall = ev => {
    ev.preventDefault();
    getProducts().then(products => {
      this.setState({ products });
    });
  };


  handleClick = ev => {
    ev.preventDefault();
    getCategoryProduct(ev.currentTarget.value).then(products => {
      if (products) {
        this.setState({ search: false });
        this.setState({ products });
        // console.log(this.state.products);
      }


    });
  };
  onchange(event) {
    event.preventDefault();
    if (event.target.value) {
      let product_name = event.target.value;
      this.setState({ searchtext: event.target.value });
      product(product_name).then(products => {
        this.setState({ products });
        this.setState({ search: true });
      });
    } else {
      getProducts().then(products => {
        this.setState({ products });
        this.setState({ search: false });
      });
    }
  }

  render() {
    const { products } = this.state;
    const { category } = this.state;
    const { search } = this.state;
    const { searchtext } = this.state;
    return (
      <div className="">
        <br />
        <div class="gyu">
          <div class="uio nav">
            <div className="container ">
              <div className="row">
                <div className="col-lg-6">
                  <Navbar
                    collapseOnSelect
                    className="navbar-expand mlp"
                    expand="lg"
                    variant="dark"
                  >
                <Navbar.Toggle
                  aria-controls="responsive-navbar-nav"
                  children={
                    <p className="eri"
                      style={{
                        fontFamily: "noir",
                        fontSize: "29px"
                      }}
                    >
                      <FaStore />
                    </p>
                  }
                  style={col}
                />
                    <Navbar.Collapse id="responsive-navbar-nav" >
                    <Nav className="mr-auto"></Nav>

                      <Nav className=" mt-0">

                      <button
                        onClick={this.getall}

                        style={btu}
                      >
                        <h2
                          class="rtt  "
                          style={{
                            fontSize: "20px",
                            fontFamily: "noir"
                          }}
                        >
                        <b class="ico"><FaStore/> </b>&nbsp; &nbsp;
                        &nbsp;
                        </h2>
                      </button>

                        {category == null ? (
                          <h2
                            class="rtt mt-3 "
                            style={{
                              fontSize: "16px",
                              fontFamily: "noir"
                            }}
                          >
                            <b >Loading Available Category!!! </b>&nbsp; &nbsp;
                            &nbsp;
                          </h2>
                        ) : (



                          category.map((category, index) => (
                            <button
                              onClick={this.handleClick}
                              value={category.id}
                              style={btu}
                            >
                              <h2
                                class="rtt  "
                                style={{
                                  fontSize: "16px",
                                  fontFamily: "noir"
                                }}
                              >
                                <b class="rer">{category.category_name} </b>&nbsp; &nbsp;
                                &nbsp;
                              </h2>
                            </button>
                          ))
                        )}
                      </Nav>
                    </Navbar.Collapse>
                  </Navbar>
                </div>

                <div className="col-lg-6 float-left mt-1">
                  <Nav.Link href="" className="text-dark  ">
                    <Form.Group as={Col} md="12" className="sio">
                      <InputGroup>
                        <InputGroup.Prepend>
                          <InputGroup.Text style={uyh} id="inputGroupPrepend">
                            <FaSearch style={{ color: "#fdfdfe" }} />
                          </InputGroup.Text>
                        </InputGroup.Prepend>
                        <Form.Control
                          type="text"
                          placeholder="Enter a Product Name Here!"
                          aria-describedby="inputGroupPrepend"
                          onChange={this.onchange}
                          style={uyw}
                          class="sio ml-5 float-left"
                          required
                        />
                      </InputGroup>
                    </Form.Group>
                  </Nav.Link>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container mt-5 ">
          <div class="row">
            <div className="col-lg-12"></div>

            <div className="col-md-12 mt-4">
              {search === true ? (
                <h3
                  style={{ fontSize: "18px" }}
                  className="card-title mb-5 ml-3"
                >
                  Search Results for {searchtext} :
                </h3>
              ) : (
                <h2></h2>
              )}
              {/* <hr /> */}
              <div class="row " id="products">
                {!products.length  ? (
                  <h1 className="text-center ml-4 mb-4" style={{ fontSize: "20px" }}>
                  No Item Available!!
                  </h1>
                ) : (
                  products.map((product, index) => (
                    <ProductItem product={product} key={index} />
                  ))
                )}
              </div>

              {/* <hr /> */}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
