import { login,productby_id } from "../repository";
import swal from "sweetalert";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
import { FaSignInAlt, FaLock, FaKey } from "react-icons/fa";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col,
  Carousel
} from "react-bootstrap/";
import image from "./image/watch41.png";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      quantity: 1,
      dr4:"none",
      drt:"",
      product:[]
    };
  }


componentDidMount(){
    let id = localStorage.getItem("pro_id");
    productby_id(id).then(product=>{
      this.setState({product});
      console.log(this.state.product);
    })

}


  addToCart = () => {
{this.state.product.map((product,index) => {
if(parseInt(product.type) === 2){


              let cart = localStorage.getItem("cart")
                ? JSON.parse(localStorage.getItem("cart"))
                : {};
          console.log(cart);
              let id = product.id.toString();
              cart[id] = cart[id] ? cart[id] : 0;
              console.log(cart[id]);
              console.log(id);

              let qty = parseInt(cart[id]) + parseInt(this.state.quantity);
              console.log(qty);
              if( qty >= 2){
                swal(
                  "Product Already Exist in Cart",
                  "Can't Re-Cart this Item!!",
                  "info"
                );
              }else{
              if (product.available_quantity < qty) {
                swal(
                  "Not Enough Item",
                  "We Don't Have Enough Of this Product Available!!",
                  "error"
                );
              } else {
                cart[id] = qty +"-SA";
                swal("Added To Cart!", "Product Added To Cart!", "success");
              }


              localStorage.setItem("cart", JSON.stringify(cart));
  }

















}else{

  swal("Select a Size", {
    buttons: {
      M: {
        text: "M",
        value: "M"
      },
      L: {
        text: "L",
        value: "L"
      },
      XL: {
        text: "XL",
        value: "XL"
      },
      cancel: true
    }
  }).then(value => {
      if(value==="M"){
          let cart = localStorage.getItem("cart")
            ? JSON.parse(localStorage.getItem("cart"))
            : {};
          console.log(cart);
          let id = product.id.toString();
          cart[id] = cart[id] ? cart[id] : 0;
          console.log(cart[id]);
          console.log(id);

          let qty = parseInt(cart[id]) + parseInt(this.state.quantity);
          console.log(qty);
          if( qty >= 2){
            swal(
              "Product Already Exist in Cart",
              "Can't Re-Cart this Item!!",
              "info"
            );
          }else{
          if (product.available_quantity < qty) {
            swal(
              "Not Enough Item",
              "We Don't Have Enough Of this Product Available!!",
              "error"
            );
          } else {
            cart[id] = qty +"-m";
            swal("Added To Cart!", "Product Added To Cart!", "success");
          }

          localStorage.setItem("cart", JSON.stringify(cart));
}
  }else if(value==="L"){


            let cart = localStorage.getItem("cart")
              ? JSON.parse(localStorage.getItem("cart"))
              : {};
        console.log(cart);
            let id = product.id.toString();
            cart[id] = cart[id] ? cart[id] : 0;
            console.log(cart[id]);
            console.log(id);

            let qty = parseInt(cart[id]) + parseInt(this.state.quantity);
            console.log(qty);
            if( qty >= 2){
              swal(
                "Product Already Exist in Cart",
                "Can't Re-Cart this Item!!",
                "info"
              );
            }else{
            if (product.available_quantity < qty) {
              swal(
                "Not Enough Item",
                "We Don't Have Enough Of this Product Available!!",
                "error"
              );
            } else {
              cart[id] = qty +"-l";
              swal("Added To Cart!", "Product Added To Cart!", "success");
            }


            localStorage.setItem("cart", JSON.stringify(cart));
}
}else if (value==="XL") {
