import React from "react";
import "./style.css";
export default class CartItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1
    };
  }

  render() {
    const { product } = this.props;
    return (
      <div className="card" style={{ marginBottom: "10px" }}>
        <div className="card-body">
          <div class="row">
            <div class="col-md-4">
              <img
                style={{ width: "200px", height: "200px" }}
                className="image-responsive hov"
                src={require(`${product.image_path}`)}
                alt="img"
              />
            </div>
          </div>
          <br />
          <br />
          <h1 className="card-title rtt" style={{ fontSize: "20px" }}>
            <b> {product.name}</b>
          </h1>
          <h6 className="card-text" style={fv}>
            <b>price: â‚¦{product.price}</b>
          </h6>
          <h5 className="card-text" style={fv}>
            <span className="card-text text-primary fv">
              <b> Quantity:</b>
              <span class="badge badge-primary">{product.qty}</span>
            </span>
          </h5>
          <button
            className="btn btn-sm btn-outline-danger float-right "
            onClick={() => this.props.remove(product)}
          >
            <b>Remove</b>
          </button>
        </div>
      </div>
    );
  }
}
var fv = {
  fontSize: "15px"
};
