import React from "react";
import { login } from "../repository";
import swal from "sweetalert";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaSignInAlt, FaLock, FaKey } from "react-icons/fa";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col,
  Carousel
} from "react-bootstrap/";
import image from "./image/watch41.png";
export default class Product extends React.Component {
  constructor() {
    super();
    this.state = { email: "", password: "" };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.submitLogin = this.submitLogin.bind(this);
  }

  handleInputChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitLogin(event) {
    event.preventDefault();
    login(this.state)
      .then(token => (window.location = "/checkout"))
      .catch(err => swal(err, "Error Occured", "error"));
  }

  render() {
    return (
      <div>
      <div class="container mt-4 mb-5">
      <div class="row">

      <div class="col-md-3">
      <Carousel controls={true} indicators={true} interval={true} pause={false} slide={true} fade={false} >
        <Carousel.Item>
          <div className="">

            {" "}
            <img
              className="image-responsive jjk"
              src={image}
              alt="img"
            />


          </div>
        </Carousel.Item>

        <Carousel.Item>
          <div className="">

            {" "}
            <img
              className="image-responsive jjk"
              src={image}
              alt="img"
            />

          </div>
        </Carousel.Item>
      </Carousel>

      </div>

      <div class="col-md-5 mt-3">
      <h4 class="text-center jjk2">Name: Daniel Wellington</h4>

        <h4 class="text-center jjk3">Price:  4,500</h4>
          <h4 class="text-center jjk5">Seller: Nginx Limited</h4>
          <h4 class="text-center jjk6">S/A: One Suites All</h4>

      </div>

      <div class="col-md-4 mt-3">

<h4 class="text-center jjk2">Return And Refund</h4>
      </div>

      </div>
      </div>











        </div>

    );
  }
}
