import React from "react";
import { isAuthenticated, getCartProducts } from "../repository";
import { Redirect, Link } from "react-router-dom";
import swal from "sweetalert";
import {
  FaCartArrowDown,
  FaUserPlus,
  FaBars,
  FaCheckSquare,
  FaStore,
  FaSeedling,
  FaSearch,
  FaLock,
  FaArchive
} from "react-icons/fa";
import "./style.css";
export default class Checkout extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      total: 0
    };
  }

  componentWillMount() {
    let cart = localStorage.getItem("cart");
    if (!cart) return;
    getCartProducts(cart).then(products => {
      let total = 0;
      for (var i = 0; i < products.length; i++) {
        total += products[i].price * products[i].qty;
      }
      this.setState({ products, total });
    });
  }

  render() {
    if (!isAuthenticated()) return <Redirect to="/login" />;
    const { products, total } = this.state;
    return (
      <div>
        <br />
        <br />
        <br />
        <br />
        <div style={nav}></div>
        <div className=" container mt-4">
          <h3>
            <b style={{ fontSize: "21px" }}>
              CheckOut <FaArchive />
            </b>
          </h3>
          <br />
          <br />
          {products.map((product, index) => (
            <div key={index}>
              <p style={rt} class="uu">
                <b style={{ fontSize: "23px" }}>{product.name}</b>
                <br />
                <span class="badge badge-success">
                  <small> quantity: {product.qty}</small>
                  <br />
                </span>

                <span className="float-right text-dark">
                  ₦{product.qty * product.price}
                </span>
              </p>
              <hr />
            </div>
          ))}
          <hr />
          {products.length ? (
            <div>
              <h4 style={rt}>
                Total Amount:
                <span className="float-right text-dark">₦{total}</span>
              </h4>
              <hr />
            </div>
          ) : (
            ""
          )}
          {!products.length ? (
            <h3 className="text-dark">
              <b>No item on the cart</b>
            </h3>
          ) : (
            ""
          )}
          {products.length ? (
            <button
              className="btn btn-sm btn-outline-success float-right"
              onClick={() => swal("Proceed to Pay")}
            >
              <b>Pay</b>
            </button>
          ) : (
            ""
          )}
          <Link to="/">
            <button
              className="btn btn-outline-danger btn-sm float-right"
              style={{ marginRight: "10px" }}
            >
              <b>Cancel</b>
            </button>
          </Link>
          <br />
          <br />
          <br />
        </div>
      </div>
    );
  }
}
var nav = {
  backgroundColor: "#fdfdfe",
  width: "100%",
  height: "65px"
};
var lp = {
  fontSize: "25px"
};

var rt = {
  fontSize: "20px"
};
