        {products.length ? (
          <div>
            <h5 class="text-dark">
              Total Amount:
              <span className="float-right text-dark">â‚¦{total}</span>
            </h5>
            <hr />
          </div>
        ) : (
          ""
        )}

        {!products.length ? (
          <h3 className="text-dark">No item on the cart!!!</h3>
        ) : (
          ""
        )}
        <Link to="/checkout">
          <button className="btn btn-sm btn-success float-right">
            <h5>Checkout</h5>
          </button>
        </Link>
        <button
          className="btn btn-danger btn-sm float-left"
          onClick={this.clearCart}
          style={{ marginRight: "10px" }}
        >
          <h6>Clear Cart</h6>
        </button>
        <br /> */}
      </div>
    );
  }
}

