import React, { Component } from "react";
import { Carousel, Button, ButtonToolbar, Card } from "react-bootstrap";
import tree from "./image/marketplace.jpg";
import pro1 from "./image/pro1.jpg";
import pro2 from "./image/pro2.jpg";
import pro3 from "./image/pro3.jpg";
import { FaArrowsAlt, FaArrowAltCircleRight, FaUserPlus } from "react-icons/fa";
import "./style.css";
export default class Carosel extends Component {
  render() {
    return (
      <div>
        <Carousel controls={false}>
          <Carousel.Item>
            <img
              className="d-block w-100 image-responsive"
              style={img}
              src={tree}
              alt="Third slide"
            />
          </Carousel.Item>
        </Carousel>

        <div className="container mt-5">
          <div className="row">
            {/* <h1 className="text-center text-dark" style={{ fontSize: "23px" }}>
              Featured Products
            </h1> */}

            <div className="col-md-4 p-3">
              <Card>
                <Card.Body>
                  <img
                    src={pro1}
                    alt="fjf"
                    style={{ width: "100%", height: "250px" }}
                  />
                  <h1
                    className="text-left mt-3 text-dark"
                    style={{ fontSize: "18px" }}
                  >
                    Your product's name
                    <br />
                    ₦19.99
                  </h1>
                </Card.Body>
              </Card>
            </div>

            <div className="col-md-4 p-3">
              <Card>
                <Card.Body>
                  <img
                    src={pro2}
                    alt="fjf"
                    style={{ width: "100%", height: "250px" }}
                  />
                  <h1
                    className="text-left mt-3 text-dark"
                    style={{ fontSize: "18px" }}
                  >
                    Your product's name
                    <br />
                    ₦19.99
                  </h1>
                </Card.Body>
              </Card>
            </div>

            <div className="col-md-4 p-3">
              <Card>
                <Card.Body>
                  <img
                    src={pro3}
                    alt="fjf"
                    style={{ width: "100%", height: "250px" }}
                  />
                  <h1
                    className="text-left mt-3 text-dark"
                    style={{ fontSize: "18px" }}
                  >
                    Your product's name
                    <br />
                    ₦19.99
                  </h1>
                </Card.Body>
              </Card>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

var img = {
  position: "cover",
  height: "400px"
};
