import React from "react";
import ProductItem from "./ProductItem";
import {
  getProducts,
  product,
  category,
  getCategoryProduct
} from "../repository";
import Spinner from "react-bootstrap/Spinner";
import {
  FaArrowAltCircleDown,
  FaSearch,
  FaServicestack,
  FaBars,
  FaStore,
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/pro1.jpg";
import "./style.css";
import "./footer.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: null,
      loader: false,
      category: null,
      search: false,
      searchtext: ""
    };
    this.onchange = this.onchange.bind(this);
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });

    category().then(category => {
      this.setState({ category });
      console.log(this.state.category);
    });
  }

  handleClick = ev => {
    ev.preventDefault();
    getCategoryProduct(ev.currentTarget.value).then(products => {
      if (products) {
        this.setState({ search: false });

        this.setState({ products });
        console.log(product);
      }
    });
  };
  onchange(event) {
    event.preventDefault();
    if (event.target.value) {
      let product_name = event.target.value;
      this.setState({ searchtext: event.target.value });
      product(product_name).then(products => {
        this.setState({ products });
        this.setState({ search: true });
      });
    } else {
      getProducts().then(products => {
        this.setState({ products });
        this.setState({ search: false });
      });
    }
  }

  render() {
    const { products } = this.state;
    const { category } = this.state;
    const { search } = this.state;
    const { searchtext } = this.state;
    return (
      <div className="">
        <br />

        <div className="gyu">
        <Navbar
          collapseOnSelect
          className=""
          expand="lg"
          style={nav}
          variant="dark"
        >
          <Link to="/" style={{ textDecoration: "none" }}>
            <Nav className="ml-3 mt-2">
              <h1
                class="link mt-1"
                style={{
                  fontFamily: "Work sans,San serif",
                  fontSize: "39px",
                  textDecoration: "none"
                }}
              >
                <b style={{ fontSize: "29px" }}>Fashion</b>
              </h1>
            </Nav>
          </Link>

          <Navbar.Toggle
            aria-controls="responsive-navbar-nav"
            children={
              <b
                style={{
                  fontFamily: "Work sans,San serif",
                  fontSize: "29px"
                }}
              >
                <FaBars />
              </b>
            }
            style={col}
          />

          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto"></Nav>

            <Nav className="ml-0">
              <Nav.Link
                eventKey={2}
                href="/market"
                style={io}
                className="text-dark mt-4"
              >
                <h4 className="lin ">
                  <b>Market</b>
                </h4>
              </Nav.Link>
              <Nav.Link href="/cart" style={io} className="text-dark  mt-4">
                <h4 className="lin">
                  <b>Cart </b>&nbsp;
                </h4>
              </Nav.Link>

              <Nav.Link href="/checkout" style={io} className="text-dark mt-4">
                <h4 className="lin">
                  <b>Checkout</b>
                </h4>{" "}
                &nbsp;
              </Nav.Link>

              {!auth ? (
                <Nav.Link
                  eventKey={2}
                  href="/login"
                  style={io}
                  className="text-dark mt-4"
                >
                  <h4 className="lin">
                    <b>Login</b>
                  </h4>{" "}
                  &nbsp;
                </Nav.Link>
              ) : (
                <Nav.Link
                  eventKey={2}
                  onClick={this.logOut}
                  style={io}
                  className="text-dark mt-4"
                >
                  <b className="lin">
                    <h4>
                      <b>Logout</b>
                    </h4>
                  </b>
                </Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div>
        <div className="container mt-5 ">
          <div class="row">
            <div className="col-lg-12"></div>

            <div className="col-md-12 mt-4">
              {search === true ? (
                <h3
                  style={{ fontSize: "18px" }}
                  className="card-title mb-5 ml-3"
                >
                  Search Results for {searchtext} :
                </h3>
              ) : (
                <h2></h2>
              )}
              {/* <hr /> */}
              <div class="row" id="products">
                {products === null ? (
                  <h1 className="text-center mb-3" style={{ fontSize: "20px" }}>
                    Loading Available Items!!!
                  </h1>
                ) : (
                  products.map((product, index) => (
                    <ProductItem product={product} key={index} />
                  ))
                )}
              </div>

              {/* <hr /> */}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
