import React from "react";
import "./style.css";
export default class CartItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1
    };
  }

  render() {
    const { product } = this.props;
    return (
      <div
        className="card"
        style={{ marginBottom: "10px", backgroundColor: "#fdfdfe" }}
      >
        <div className="card-body">
          <h1 className="card-title rtt" style={{ fontSize: "27px" }}>
            <b>{product.name}</b>
          </h1>
          <h6 className="card-text" style={fv}>
            <b> price: â‚¦{product.price}</b>
          </h6>
          <h5 className="card-text" style={fv}>
            <span className="card-text text-primary fv">
              <b> Quantity:</b>
              <span class="badge badge-primary">{product.qty}</span>
            </span>
          </h5>
          <button
            className="btn btn-sm btn-outline-danger  "
            onClick={() => this.props.remove(product)}
          >
            <b>Remove</b>
          </button>
          <img
            className="image-responsive hov iop float-right"
            style={{ width: "170px", height: "160px" }}
            src={require(`${product.image_path}`)}
            alt="img"
          />
        </div>
      </div>
    );
  }
}
