import React from "react";
import { login } from "../repository";
import swal from "sweetalert";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaSignInAlt, FaLock, FaKey } from "react-icons/fa";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
export default class Login extends React.Component {
  constructor() {
    super();
    this.state = { email: "", password: "" };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.submitLogin = this.submitLogin.bind(this);
  }

  handleInputChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitLogin(event) {
    event.preventDefault();
    login(this.state)
      .then(token => (window.location = "/checkout"))
      .catch(err => swal(err, "Error Occured", "error"));
  }

  render() {
    return (
      <div>
        <br />
        <br />
        <br />
        <br />

        <div className="container mb-5">
          <h5 style={mn} className="ml-4">
            &nbsp; Login &nbsp; <FaLock />
          </h5>
          <hr />
          <div className="col-sm-8 col-sm-offset-2">
            <div className="panel panel-primary">
              <div className="panel-heading"></div>
              <div className="panel-body">
                <form onSubmit={this.submitLogin}>
                  <div class="col-md-8">
                    <div className="form-group">
                      <label>
                        <h5>Email:</h5>
                      </label>

                      <input
                        type="text"
                        className="form-control"
                        name="email"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>

                  <div class="col-md-8">
                    {" "}
                    <div className="form-group">
                      <label>
                        <h5>Password:</h5>
                      </label>
                      <input
                        type="password"
                        className="form-control"
                        name="password"
                        onChange={this.handleInputChange}
                      />
                    </div>
                  </div>

                  <button
                    type="submit"
                    className="btn btn-sm btn-outline-secondary ml-3"
                  >
                    Login
                  </button>

                  <a
                    href="/signup"
                    class="btn mt-3 ml-4 btn-sm btn-outline-secondary"
                  >
                    Signup
                  </a>
                </form>
              </div>
            </div>
          </div>

          <div className="col-md-4 mb-5 float-righ right">
            <Card>
              {/* <Card.Header style={{ backgroundColor: "#fdfdf9" }}> */}
              <h4 class="text-center">
                {" "}

              </h4>
              {/* </Card.Header> */}

              <Card.Body>
                <h4
                  class="text-dark text-center rtt"
                  style={{
                    fontSize: "20px",
                    fontFamily: "Work sans,San serif",
                    textTransform: "capitalize"
                  }}
                >
                  <b> g4</b>
                </h4>

                <hr />

                <h5
                  class="text-dark text-center "
                  style={{ fontSize: "17px", fontFamily: "Work sans,San serif" }}
                >

                    <p
                      className="text-danger"
                      style={{
                        fontSize: "18px",
                        fontFamily: "Work sans,San serif"
                      }}
                    ></p>

                </h5>


                <h5
                  class="text-dark text-center"
                  style={{ fontSize: "16px", fontFamily: "Work sans,San serif" }}
                >
                  ₦3000
                </h5>

                <h5 className="card-text text-center" >
                  <span className="card-text text-secondary fv">
                    Quantity:
                    <span class="badge badge-secondary">3</span>
                  </span>
                </h5>

                <h4 class="text-center">
                  {" "}
                  <button
                    className="btn btn-sm  btn-outline-secondary  "

                  >
                    Remove
                  </button>
                </h4>
              </Card.Body>
            </Card>

          </div>


          </div>
      </div>
    );
  }
}
var mn = {
  fontSize: "20px",
  marginLeft: "20px"
};

var nav = {
  backgroundColor: "#f7f7fde0",
  width: "100%",
  height: "15px"
};
