import React from "react";
import { login, Signup, Check } from "../repository";
import swal from "sweetalert";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaSignInAlt, FaLock, FaKey } from "react-icons/fa";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
export default class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      firstname: "",
      lastname: "",
      password: "",
      email: "",
      phone: "",
      address: ""
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.submitSignup = this.submitSignup.bind(this);
  }

  handleInputChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  Checksign() {
    Check(this.state).then(msg => swal(msg, "Error Occured", "error"));
  }

  submitSignup(event) {
    event.preventDefault();
    if (this.state.firstname === "") {
      swal("Empty Firstname", "Error Occured", "error");
    } else if (this.state.lastname === "") {
      swal("Empty Lastname", "Error Occured", "error");
    } else if (this.state.email === "") {
      swal("Empty Email", "Error Occured", "error");
    } else if (this.state.phone === "") {
      swal("Empty Phone", "Error Occured", "error");
    } else if (this.state.address === "") {
      swal("Empty Address", "Error Occured", "error");
    } else if (this.state.password === "") {
      swal("Empty Password", "Error Occured", "error");
    } else {
      Check(this.state).then(msg => swal(msg, "", "info"));
      //   Signup(this.state)
      //     .then(token => (window.location = "/market"))
      //     .catch(err => swal(err, "Error Occured", "error"));
    }
  }
  render() {
    return (
      <div>
        <br />
        <br />
        <br />
        <br />
        <div style={nav}></div>
        <div className="container mt-5 mb-5">
          <h5 style={mn} className="ml-4">
            Signup &nbsp; <FaLock />
          </h5>
          <hr />
          <div className="col-sm-8 col-sm-offset-2">
            <div className="panel panel-primary">
              <div className="panel-heading"></div>
              <div className="panel-body">
                <form onSubmit={this.submitSignup}>
                  <div class="row">
                    <div class="col-md-6"></div>
                  </div>

                  <div className="form-group">
                    <label>
                      <h5>Firstname:</h5>
                    </label>

                    <input
                      type="text"
                      className="form-control"
                      name="firstname"
                      onChange={this.handleInputChange}
                    />
                  </div>

                  <div className="form-group">
                    <label>
                      <h5>Lastname:</h5>
                    </label>

                    <input
                      type="text"
                      className="form-control"
                      name="lastname"
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>
                      <h5>Email:</h5>
                    </label>

                    <input
                      type="text"
                      className="form-control"
                      name="email"
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>
                      <h5>Phone:</h5>
                    </label>

                    <input
                      type="text"
                      className="form-control"
                      name="phone"
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>
                      <h5>Address:</h5>
                    </label>

                    <input
                      type="text"
                      className="form-control"
                      name="address"
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>
                      <h5>Password:</h5>
                    </label>
                    <input
                      type="password"
                      className="form-control"
                      name="password"
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <button
                    type="submit"
                    className="btn btn-sm btn-outline-success"
                  >
                    <b class="">Signup</b>
                  </button>
                  <a
                    href="/login"
                    class="btn mt-3 ml-4 btn-sm btn-outline-primary"
                  >
                    <b>Login</b>
                  </a>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
var mn = {
  fontSize: "20px"
};

var nav = {
  backgroundColor: "#eef9",
  width: "100%",
  height: "20px"
};
