import React from "react";
import ProductItem from "./ProductItem";
import { getProducts } from "../repository";
import { FaCartPlus, FaUserPlus, FaCaretDown, FaBars } from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/cer.jpg";
import "./style.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    };
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });
  }

  render() {
    const { products } = this.state;
    return (
      <div className="container mt-5 ">
        <div class="row">
          <div className="col-lg-12">
            <Navbar
              collapseOnSelect
              className=" "
              expand="lg"
              style={nav}
              variant="dark"
            >
              <Navbar.Toggle
                aria-controls="responsive-navbar-nav"
                children={
                  <h1>
                    <FaBars />
                  </h1>
                }
                style={col}
              />
              <Navbar.Collapse id="responsive-navbar-nav">
                {/* <Nav className="mr-auto"></Nav> */}
                <Nav className="mr-5">
                  <Nav.Link
                    eventKey={2}
                    href=""
                    style={io}
                    className="text-dark mt-4"
                  >
                    <b className="lin">
                      <h6>Fruits</h6>
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>Vegetables</h6> &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>Cereals</h6> &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>Grains</h6> &nbsp;
                    </b>
                  </Nav.Link>
                  <Nav.Link href="" style={io} className="text-dark mt-4">
                    <b className="lin">
                      <h6>Beverages</h6> &nbsp;
                    </b>
                  </Nav.Link>
                </Nav>
              </Navbar.Collapse>
            </Navbar>
          </div>

          <div className="col-md-9 mt-4">
            <div className="col-lg-6">
              <Accordion defaultActiveKey="1">
                <div class="col-lg-12 col-md-2 mt-5 col-sm-2 mb-3">
                  <Card>
                    <Card.Header style={col}>
                      <CustomToggle eventKey="0">
                        <h4 class="text-dark text-center">
                          Category&nbsp;&nbsp; <FaCaretDown />
                        </h4>

                        {/* <h1 style={ui}>
                        <FaCaretDown />
                      </h1> */}
                      </CustomToggle>
                    </Card.Header>
                  </Card>
                </div>
                <Accordion.Collapse eventKey="0">
                  <Card>
                    <Card.Body>
                      <h4 class="text-dark text-center">Fruits</h4>
                      <hr />
                      <h4 class="text-dark text-center">Vegetables</h4>
                      <hr />
                      <h4 class="text-dark text-center">Cereals</h4>
                      <hr />
                      <h4 class="text-dark text-center">Grains</h4>
                      <hr />
                      <h4 class="text-dark text-center">Beverages</h4>
                      <hr />
                    </Card.Body>
                  </Card>
                </Accordion.Collapse>
              </Accordion>
            </div>
            {/* <h3 className="card-title">Available Products</h3> */}
            <hr />
            <div class="row">
              {products.map((product, index) => (
                <ProductItem product={product} key={index} />
              ))}
            </div>

            <hr />
          </div>
        </div>
      </div>
    );
  }
}
var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
};

var col = {
  backgroundColor: "#eef"
};

function CustomToggle({ children, eventKey }) {
  const decoratedOnClick = useAccordionToggle(eventKey, () =>
    console.log(eventKey)
  );

  return (
    <h4 class="text-center">
      <button
        type="submit"
        style={{
          backgroundColor: "transparent",
          textAlign: "center",
          border: "0px",
          outline: "none"
        }}
        onClick={decoratedOnClick}
      >
        {children}
      </button>
    </h4>
  );
}

var io = {
  paddingLeft: "15px"
};

var col = {
  color: "black",
  fontSize: "9px",
  padding: "10px"
};

var nav = {
  backgroundColor: "#eef"
};
var img = {
  width: "180px"
  // marginBottom: "10px"
};
var ui = {
  fontSize: "20px"
};
