import React from "react";
import { Link } from "react-router-dom";
import { getCartProducts } from "../repository";
import CartItem from "./CartItem";
import "bootstrap/dist/css/bootstrap.min.css";
import swal from "sweetalert";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import {
  FaCartArrowDown,
  FaUserPlus,
  FaBars,
  FaCheckSquare,
  FaStore,
  FaSeedling,
  FaSearch,
  FaLock,

} from "react-icons/fa";
export default class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      total: 0
    };
  }

  componentWillMount() {
    let cart = localStorage.getItem("cart");
    console.log(cart);
    if (!cart) return;
    getCartProducts(cart).then(products => {
      let total = 0;
      for (var i = 0; i < products.length; i++) {
        total += products[i].price * products[i].qty;
      }
      this.setState({ products, total });
    });
  }

  removeFromCart = product => {
    swal("Are You sure you want to remove product?", {
      buttons: {
        cancel: "default",
        ok: {
          text: "Remove",
          value: "ok"
        },
        cancel: true
      }
    }).then(value => {
      switch (value) {
        case "ok":
          let products = this.state.products.filter(
            item => item.id !== product.id
          );
          let cart = JSON.parse(localStorage.getItem("cart"));
          delete cart[product.id.toString()];
          localStorage.setItem("cart", JSON.stringify(cart));
          let total = this.state.total - product.qty * product.price;
          this.setState({ products, total });
          swal("Product has been removed!");
          break;

        default:
          swal("Your Products Are Safe!!", "", "success");
          break;
      }
    });
  };

  clearCart = () => {
    localStorage.removeItem("cart");
    this.setState({ products: [] });
    swal("Your Cart Has Been Cleared!!!", "", "success");
  };

  render() {
    const { products, total } = this.state;
    return (
      <div>

      <br/>
      <div class="gyu">
        <div style={nav} class="uio">
          <div className="container ">
            <div className="row">
              <div className="col-lg-6">
                <Navbar
                  collapseOnSelect
                  className=""
                  expand="lg"
                  variant="dark"
                >
                  <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className=" mt-0">

                          <button
                            onClick={this.handleClick}

                            style={btu}
                          >
                            <h2
                              class="rtt  "
                              style={{
                                fontSize: "16px",
                                fontFamily: "Work sans"
                              }}
                            >
                              <b><marquee>New Sale Offer Now!!!</marquee>  </b>&nbsp; &nbsp;
                              &nbsp;
                            </h2>
                          </button>

                    </Nav>
                  </Navbar.Collapse>
                </Navbar>
              </div>

              <div className="col-lg-6 ml-2 float-left mt-1">
                <Nav.Link href="" className="text-dark  ">
                  <Form.Group as={Col} md="12" className="sio">
                    <InputGroup>
                      <InputGroup.Prepend>
                        <InputGroup.Text style={uyh} id="inputGroupPrepend">
                          <FaSearch style={{ color: "#fdfdfe" }} />
                        </InputGroup.Text>
                      </InputGroup.Prepend>
                      <Form.Control
                        type="text"
                        placeholder=""
                        aria-describedby="inputGroupPrepend"
                        onChange={this.onchange}
                        style={uyw}
                        class="sio ml-5 float-left"
                        required
                      />
                    </InputGroup>
                  </Form.Group>
                </Nav.Link>
              </div>
            </div>
          </div>
        </div>
      </div>


        <br />
        <br />
        <br />
        <br />

        <div class="container">
          <div class="row" id="products">
            {products.map((product, index) => (
              <CartItem
                product={product}
                remove={this.removeFromCart}
                key={index}
              />
            ))}
          </div>

          <div class="container">
            {products.length ? (
              <div>
                <h5 class="text-dark" style={{ fontSize: "20px" }}>
                  <b>Total Amount:</b>
                  <span
                    className="float-right text-dark"
                    style={{ fontSize: "21px" }}
                  >
                    <b> â‚¦{total}</b>
                  </span>
                </h5>
                <hr />
              </div>
            ) : (
              ""
            )}

            {!products.length ? (
              <h3 className="text-dark">
                <b>No item on the cart!!!</b>
              </h3>
            ) : (
              ""
            )}
            <Link to="/checkout">
              <button className="btn btn-sm btn-outline-secondary float-right">
                Checkout
              </button>
            </Link>
            <button
              className="btn btn-outline-secondary btn-sm float-left"
              onClick={this.clearCart}
              style={{ marginRight: "10px" }}
            >
              Clear Cart
            </button>
            <br />

            <br />
            <br />
          </div>
        </div>
      </div>
    );
  }
}

