import React from "react";
import ProductItem from "./ProductItem";
import {
  getProducts,
  product,
  category,
  getCategoryProduct
} from "../repository";
import Spinner from "react-bootstrap/Spinner";
import {
  FaArrowAltCircleDown,
  FaSearch,
  FaServicestack,
  FaBars
} from "react-icons/fa";
import { Link } from "react-router-dom";
import {
  Card,
  Accordion,
  useAccordionToggle,
  Navbar,
  Nav,
  Form,
  InputGroup,
  Col
} from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/cer.jpg";
import "./style.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      loader: false,
      category: []
    };
    this.onchange = this.onchange.bind(this);
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });

    category().then(category => {
      this.setState({ category });
      console.log(this.state.category);
    });
  }

  handleClick = ev => {
    ev.preventDefault();
    getCategoryProduct(ev.currentTarget.value).then(products => {
      this.setState({ products });
      console.log(product);
    });
  };
  onchange(event) {
    event.preventDefault();
    if (event.target.value) {
      let product_name = event.target.value;
      product(product_name).then(products => {
        this.setState({ products });
      });
    } else {
      getProducts().then(products => {
        this.setState({ products });
      });
    }
  }

  render() {
    const { products } = this.state;
    const { category } = this.state;
    return (
      <div className="mt-4">
        <br />
        <div style={nav}>
          <div className="container mt-5">
            <div className="row">
              <div className="col-lg-6">
                <Navbar
                  collapseOnSelect
                  className=" "
                  expand="lg"
                  variant="dark"
                >
                  {/* <Navbar.Toggle
                    aria-controls="responsive-navbar-nav"
                    children={
                      <h1 style={ju}>
                        <FaBars />
                      </h1>
                    }
                    style={col}
                  /> */}
                  <Navbar.Collapse id="responsive-navbar-nav">
                    {/* <Nav className="mr-auto"></Nav> */}
                    <Nav className="ml-5">
                      {category.map((category, index) => (
                        <button
                          onClick={this.handleClick}
                          value={category.id}
                          style={btu}
                        >
                          <h2
                            class="rtt ml-3 mt-3"
                            style={{ fontSize: "17px" }}
                          >
                            {category.category_name} &nbsp; &nbsp; &nbsp;
                          </h2>
                        </button>
                      ))}

                      {/* <Nav className="mr-auto"></Nav> */}
                    </Nav>
                  </Navbar.Collapse>
                </Navbar>
              </div>
              <div className="col-lg-6 float-left">
                <Nav.Link href="" className="text-dark mt-4  ">
                  <Form.Group as={Col} md="12" className="">
                    <InputGroup>
                      <InputGroup.Prepend>
                        <InputGroup.Text style={uyh} id="inputGroupPrepend">
                          <FaSearch />
                        </InputGroup.Text>
                      </InputGroup.Prepend>
                      <Form.Control
                        type="text"
                        placeholder="Enter a Product Name Here"
                        aria-describedby="inputGroupPrepend"
                        onChange={this.onchange}
                        style={uyw}
                        class="sio ml-5 float-left"
                        required
                      />
                    </InputGroup>
                  </Form.Group>
                </Nav.Link>
              </div>
            </div>
          </div>
        </div>
        <div className="container mt-5 ">
          <div class="row">
            <div className="col-lg-12"></div>

            <div className="col-md-12 mt-4">
              {/* <h3 className="card-title">Available Products</h3> */}
              {/* <hr /> */}

              <div class="row" id="products">
                {products.map((product, index) => (
                  <ProductItem product={product} key={index} />
                ))}
              </div>

              {/* <hr /> */}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
};

var col = {
  backgroundColor: "#eef9"
};

function CustomToggle({ children, eventKey }) {
  const decoratedOnClick = useAccordionToggle(eventKey, () =>
    console.log(eventKey)
  );

  return (
    <h4 class="text-center">
      <button
        type="submit"
        style={{
          backgroundColor: "transparent",
          textAlign: "center",
          border: "0px",
          outline: "none"
        }}
        onClick={decoratedOnClick}
      >
        {children}
      </button>
    </h4>
  );
}

