import React from "react";
import ProductItem from "./ProductItem";
import { getProducts } from "../repository";
import { FaCartPlus, FaUserPlus, FaCaretDown } from "react-icons/fa";
import { Link } from "react-router-dom";
import { Card, Accordion, useAccordionToggle } from "react-bootstrap/";
import "bootstrap/dist/css/bootstrap.min.css";
import cer from "./image/cer.jpg";
import "./style.css";
export default class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    };
  }

  componentWillMount() {
    getProducts().then(products => {
      this.setState({ products });
    });
  }

  render() {
    const { products } = this.state;
    return (
      <div className="container mt-5 ">
        <div class="row">
          <div className="col-md-3 mt-5"></div>
          <div className="col-md-9 mt-4">
            {/* <h3 className="card-title">Available Products</h3> */}
            <hr />
            <div class="row">
              {products.map((product, index) => (
                <ProductItem product={product} key={index} />
              ))}
            </div>

            <hr />
          </div>
        </div>
      </div>
    );
  }
}
var img = {
  width: "200px",
  height: "150px",
  marginTop: "9px",
  borderRadius: "5px"
};

var col = {
  backgroundColor: "#eef"
};

function CustomToggle({ children, eventKey }) {
  const decoratedOnClick = useAccordionToggle(eventKey, () =>
    console.log(eventKey)
  );

  return (
    <h4 class="text-center">
      <button
        type="submit"
        style={{
          backgroundColor: "transparent",
          textAlign: "center",
          border: "0px",
          outline: "none"
        }}
        onClick={decoratedOnClick}
      >
        {children}
      </button>
    </h4>
  );
}

var ui = {
  fontSize: "20px"
};
