import React, { Component } from "react";
import { Carousel, Button, ButtonToolbar } from "react-bootstrap";
import tree from "../image/s2.jpg";
import { FaArrowsAlt, FaArrowAltCircleRight, FaUserPlus } from "react-icons/fa";
import "../style.css";
export default class Carosel extends Component {
  render() {
    return (
      <div>
        <Carousel controls={false}>
          <Carousel.Item>
            <img
              className="d-block  img-responsive"
              style={img}
              src={tree}
              alt="Third slide"
            />
          </Carousel.Item>
        </Carousel>

        <div class="top-left text-light">
          <h1>
            Connect to a world of opportunities in
            <br /> Agriculture in Nigeria
          </h1>
          <br />
          <h4 className="yu">
            Agriculture is the “green oil”. In Nigeria alone, Agriculture is a
            $50bn opportunity with great potential. “Africa’s future
            billionaires and millionaires will make their money from
            agriculture,” said Dr. Adesina. Will you be one of them?
          </h4>
          <br />
          <div className="but">
            <ButtonToolbar>
              <Button variant="primary" className="ui" size="md">
                Sign Up <FaUserPlus />
              </Button>

              <Button variant="success" className="ui" size="md">
                Learn More <FaArrowAltCircleRight />
              </Button>
            </ButtonToolbar>
          </div>
        </div>
      </div>
    );
  }
}

var se = {};
var img = {
  position: "contain"
};
